BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "Articles" (
	"id"	INTEGER NOT NULL UNIQUE,
	"title"	TEXT NOT NULL,
	"user"	INTEGER NOT NULL,
	"extract"	TEXT NOT NULL UNIQUE,
	"text"	TEXT NOT NULL UNIQUE,
	"likes"	INTEGER DEFAULT 0,
	"comments"	NUMERIC DEFAULT 0,
	"date"	DATETIME NOT NULL UNIQUE,
	"visuals"	INTEGER DEFAULT 0,
	"category"	INTEGER NOT NULL DEFAULT 1,
	"author"	INTEGER NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("author") REFERENCES "Authors"("id"),
	FOREIGN KEY("category") REFERENCES "Categories"("id"),
	FOREIGN KEY("user") REFERENCES "Users"("id")
);
CREATE TABLE IF NOT EXISTS "Authors" (
	"id"	INTEGER NOT NULL UNIQUE,
	"user"	INTEGER NOT NULL UNIQUE,
	"age"	INTEGER NOT NULL,
	"insta"	TEXT,
	"email"	TEXT,
	"presentation"	TEXT NOT NULL,
	"nickname"	TEXT NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("user") REFERENCES "Users"("id") ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "Categories" (
	"id"	INTEGER NOT NULL UNIQUE,
	"name"	TEXT NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "Comments" (
	"id"	INTEGER NOT NULL UNIQUE,
	"text"	TEXT NOT NULL,
	"user"	INTEGER NOT NULL,
	"article"	INTEGER NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("article") REFERENCES "Articles"("id"),
	FOREIGN KEY("user") REFERENCES "Users"("id")
);
CREATE TABLE IF NOT EXISTS "Interactions" (
	"id"	INTEGER NOT NULL UNIQUE,
	"user"	INTEGER NOT NULL,
	"article"	INTEGER NOT NULL,
	"visual"	INTEGER NOT NULL DEFAULT 1,
	"like"	INTEGER NOT NULL DEFAULT 0,
	"favourite"	INTEGER NOT NULL DEFAULT 0,
	"comment"	INTEGER NOT NULL DEFAULT 0,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("article") REFERENCES "Articles"("id"),
	FOREIGN KEY("user") REFERENCES "Users"("id"),
	UNIQUE("user", "article")
);
CREATE TABLE IF NOT EXISTS "Users" (
	"id"	INTEGER NOT NULL UNIQUE,
	"username"	TEXT NOT NULL UNIQUE,
	"password"	TEXT,
	"name"	INTEGER,
	"salt"	TEXT NOT NULL,
	"type"	TEXT NOT NULL DEFAULT 'reader' CHECK("type" IN ('admin', 'reader', 'writer')),
	"game"	INTEGER NOT NULL DEFAULT 0 CHECK("game" IN (0, 1)),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "Events" (
	"id"	INTEGER NOT NULL UNIQUE,
	"title"	TEXT NOT NULL,
	"category"	TEXT NOT NULL,
	"date"	DATETIME NOT NULL,
	"location"	TEXT NOT NULL,
	"address"	TEXT NOT NULL,
	"extract"	TEXT,
	"description"	TEXT NOT NULL,
	"capacity"	INTEGER,
	"status"	TEXT NOT NULL DEFAULT 'upcoming' CHECK("status" IN ('upcoming', 'ongoing', 'completed', 'cancelled')),
	"photo"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
INSERT INTO "Articles" ("id","title","user","extract","text","likes","comments","date","visuals","category","author") VALUES (3,'Maternità femminista',3,'Per secoli la maternità è stata la gabbia a cui le donne erano costrette. I discorsi riguardanti il desiderio di accudire o meno la prole - o per lo meno di farne o no il proprio unico scopo di vita - non erano nemmeno all’orizzonte...','La cultura patriarcale occidentale ha imposto parametri e fini fallici a tutta la popolazione, indistintamente, rappresentando la maternità come l’unico destino femminile e raffigurandola come un “handicap” che categoricamente impediva alle donne di partecipare alla sfera pubblica. Di fatto, la questione non venne nemmeno affrontata dalle prime emancipazioniste, che non misero in discussione la visione fallocentrica di questo argomento: non trattarono della possibilità di scegliere se avere figli o meno, e nemmeno di tutti i problemi che gravavano su chi, madre, lo ero già diventata, quanto più si impegnarono solamente a lottare contro l’oppressione materiale che subivano, avanzando qualche cenno alla possibilità di partecipare alla vita pubblica nonostante l’essere madri e al dolore che provavano nel vedere i propri figli partire per una guerra che nella maggior parte dei casi non li avrebbe più fatti tornare tra le braccia materne. 
Sarà solo con la seconda ondata del movimento delle donne che tutta questa costruzione artificiale inizierà a vacillare. Le femministe degli anni ’70, infatti, decisero di concentrarsi sulla messa in discussione dell’oppressione che vigeva nella dimensione simbolica delle loro vite, contestando la cultura patriarcale e i suoi contenuti, sfatando i miti che da sempre avevano descritto le donne come vuote, meri specchi muti della soggettività maschile. Proprio tra queste istanze, la riconquista della maternità vede la luce. Ciò che fino a quel momento rappresentava l’unico compito che le donne dovevano adempiere per contribuire alla società, comincia ad acquisire una simbologia diversa. Le autrici vogliono mostrare tutta la potenza della riproduzione, riappropriandosi di questa dimensione e connotandola in modo diverso e proprio, sottolineando tutta la ricchezza che poteva esservi dietro. L’istituzione fallogocentrica della maternità viene man mano distrutta per far spazio a una risignificazione che potesse darle la dignità che merita. La metà maschile dell’umanità viene accusata di aver impedito alla metà femminile di dare valore e un proprio senso a quell’esperienza. Partendo da una parte dalla dimensione esperienziale (prendiamo come esempio Carla Lonzi) e dall’altra da quella teorico-filosofica (Luce Irigaray), le donne iniziano a sfruttare in modo diverso le risorse che il vissuto dei corpi femminili (fra cui la maternità) fornisce loro, smettendo di renderle utili alla struttura patriarcale e invece usandole a proprio vantaggio per liberarsi dalla morsa che da sempre le cattura. Da ennesima componente della strutturazione gerarchica maschile, la maternità diventa resistenza che mette in crisi le narrazioni che ne sono state fatte fino a quel momento, riappropriandosi di tutte le sensazioni che la abitano e aprendo a tutte le donne la possibilità di risignificarla a proprio modo in base alla propria esperienza. La potenza generatrice diventa così perno di una pienezza femminile, laddove gli uomini hanno descritto sempre e solo vuoto, permettendo di conseguenza anche il recupero della genealogia femminile: se la generazione della vita è sempre stato un filo che collega padre e figlio, una linea che procede di uomo in uomo, in cui la donna è sempre stata solo un tramite generazionale, da adesso si recupera la verticalità tra donne, cambiando sesso a questa rappresentazione. Laddove il fallologocentrismo ha visto sempre e solo madri passive, la seconda ondata descrive le madri come soggetti attivi e desideranti, in cui arde il desiderio sessuale, quello di essere madri, quello di accudire la prole e tanto altro. In questa nuova narrazione della maternità, le donne sono generatrici non solo di figli, ma creative a tutto tondo, artefici di pensieri, idee, esperienze, teorie, ...  
La donna, dunque, non acquisisce la propria libertà grazie alla maternità, quanto più una volta che si è liberata da sé può trasmettere la propria libertà ai propri figli, liberandoli a loro volta, in modo tale che i maschi possano non diventare patriarchi di un’altra donna e le femmine possano vivere una vita diversa, connotata dalla scelta di dare senso e valore alle proprie avventure. Sancendo così l’alleanza tra la madre e il giovane figlio, si riabilita a maternità in modo tale che generare la vita non renda schiava la madre, né la sua prole.
Il patriarcato ha voluto descrivere l’ombelico come il simbolo vivente dell’allontanamento dalla madre operato dal padre tramite il taglio del cordone ombelicale alla nascita; tuttavia, arriva adesso il momento di considerare l’ombelico come l’emblema che ci accompagna per tutta la vita di quel rapporto intra-uterino che abbiamo vissuto in modo esclusivo, a cui non possiamo tornare ma che ha caratterizzato l’inizio della nostra vita, in cui la pienezza di una donna ha creato a sua volta il soggetto pieno che noi siamo oggi. Questa è la grandezza della gravidanza. Non compiere nuovamente il matricidio originario su cui si basa la cultura patriarcale, ovvero recuperare il nostro rapporto con l’origine a livello teorico, permette agli uomini di significare il proprio corpo in maniera diversa, più sana, ma soprattutto permette alle donne di significare sé stesse, di riconoscersi soggetti completi.
Nonostante poi la terza ondata del movimento femminista possa essere criticata per aver chiuso l’apertura di significazione dell’ondata precedente, io vorrei rimanere in questa dimensione di libertà di significazione di sé stessi, e a questa stessa libertà voglio appellarmi adesso: ricordiamoci sempre che ogni donna ha il diritto di fare della propria vita ciò che ritiene giusto e soddisfacente. Ad oggi, io pretendo che ogni donna abbia la possibilità di scelta su sé stessa, il che può voler dire avere figli, non averli, partorirli ma non accudirli, accudire figli non partoriti o qualsiasi altra opzione venga ritenuta adatta alla propria situazione. Madre non è chi ha una gravidanza, ma molto altro. Riappropriarsi della gravidanza in termini femministi per me significa proprio possibilità di scelta e di vivere a pieno quella scelta. Questo significa anche che dal governo del nostro Paese non mi aspetto solo degli auguri sui social media, ma aiuti concreti: un gender gap salariale più basso, maternità lavorativa garantita ed efficace, asili nido meno costosi e maggiori possibilità di accedervi, e molte altre proposte che non sono state avanzate da me, che di questo sicuramente non mi occupo, ma da attivisti e attiviste professionisti di questo campo. Dal nostro canto, invece, potremmo iniziare a cambiare la nostra stessa visione delle madri, a partire dalla nostra: oltre a regalarle i fiori e un bigliettino, dovremmo iniziare a non dare per scontato che sia suo compito cucinare, occuparsi della casa e prendere le decisioni migliori, potremmo iniziare ad aiutarla attivamente, magari permettendole di essere sé stessa, oltre che nostra madre. Sono fermamente convinta che la maternità sia una delle esperienze più difficili e totalizzanti che esistano, dunque diamole la dignità e il rispetto che merita.
',0,0,'2025-09-16 18:51:18',15,5,1),
 (4,'Il bikini, tra storia e simbologia',3,'In infinite varianti di forme, colori e taglie, è ormai abituale vedere il bikini indossato in mille occasioni diverse: il modello di costume a due pezzi è ad oggi uno dei simboli dell’estate, ma quasi ottanta anni fa è stato a dir poco una rivoluzione','Nonostante abbiamo tracce della sua esistenza addirittura risalenti all’Impero Romano del I secolo d.C. – in cui tuttavia non serviva per nuotare o abbronzarsi, quanto più per svolgere attività fisica – la sua invenzione nelle vesti di costume da bagno è tradizionalmente ricondotta agli anni Quaranta del secolo scorso. L’ex-ingegnere automobilistico Louis Réard, ai tempi famoso per la sua eccentricità, dopo aver deciso di cambiare carriera e di ereditare l’attività di commercio di lingerie della madre, diventando un esoso stilista e sarto, ha tutti gli onori e gli oneri di questa invenzione. Ispiratosi alla contemporanea moda di Saint-Tropez, dove le donne erano solite cercare di arrotolare il più possibile i propri costumi da bagno per riuscire a far brunire dal Sole una porzione di pelle più vasta possibile, il couturier presentò il suo primo modello di costume a due pezzi il 5 luglio 1946, con non poche difficoltà: il capo, in totale 30 pollici di tessuto stampato (raffigurava le prime pagine dei giornali, proprio per evocare il grande scalpore che avrebbe avuto di lì a poco), venne considerato troppo audace per le indossatrici dell’epoca. L’unica coraggiosa, o almeno la prima, fu Micheline Bernardini, ballerina “senza veli” del Casinò di Parigi, poi seguita nei vent’anni successivi da una sfilza di attrici famose, fra cui primeggiano Marilyn Monroe, Brigitte Bardot e le italiane Sophia Loren e Gina Lollobrigida, a cui si deve buona parte del successo mondiale del bikini. Réard scelse per la sua creazione un nome sicuramente particolare: il riferimento è all’isola Bikini, situata nell’arcipelago Marshall, dove proprio nello stesso periodo gli Stati Uniti stavano conducendo i loro primi esperimenti nucleari. Lo stilista aveva già previsto che la comparsa di questo nuovo modello sarebbe stata una bomba - e lo ha voluto dichiarare (quasi) letteralmente. A dire il vero, non ebbe torto: la poca quantità di tessuto e l’ombelico scoperto erano sulla bocca di tutti, vietati in alcune spiagge e criticati sulle prime pagine delle riviste. Da una parte il Vaticano non poté astenersi dal descriverlo come uno scempio, definendolo addirittura “peccaminoso”, dall’altra iniziò a diffondersi la metafora del bikini come simbolo rivoluzionario di venerazione del corpo femminile, anche grazie alla vasta quantità di attrici e donne di successo paparazzate a indossarlo durante le loro vacanze estive. 
Ad oggi il bikini è il protagonista dell’estate di tutto il Pianeta, con forme, stampe, colori, modelli e dimensioni in continua evoluzione per adeguarsi ai mutevoli gusti di chi lo indossa, ma la questione non si esaurisce qui. Ritengo necessario accendere i riflettori su ciò che questo capo rappresenta nella società odierna. Infatti, il bikini può essere visto come emblema di diverse questioni etiche.
In primo luogo, in un’ottica più ottimista, possiamo vederlo come uno dei principali vessilli dell’emancipazione femminile per eccellenza: la più ampia porzione di corpo nudo è stata lo specchio di una nuova consapevolezza e della sua rivendicazione. Un paio di decenni prima della seconda ondata del femminismo, che riprenderà in modo approfondito istanze chiaramente riconducibili a temi come questi, le donne attraverso la moda hanno voluto rappresentare di avere l’assoluto controllo sul proprio corpo e sul proprio abbigliamento, allontanandosi dagli standard, dai pregiudizi e dalle imposizioni tutte maschili a cui il patriarcato le costringeva. Il bikini era simbolo non solo del diritto all’autodeterminazione che le donne rivendicavano, ma anche della nuovissima pretesa di non essere viste solo ed esclusivamente come oggetti sessuali - tutt’oggi, purtroppo, è necessario ripetere e sottolineare che la quantità di corpo femminile scoperto non autorizza automaticamente giudizi, sguardi e tocchi estranei e non richiesti. Emblema di libertà ed emancipazione (oggi come allora), il bikini non era solo un capo d’abbigliamento, ma era considerato la controparte materiale di una sfida culturale che si stava andando a concretizzare su più livelli: religioso, economico, politico e soprattutto filosofico.
In seconda battuta, il bikini non è esente dal contemporaneo trend generale di rendere l’abbigliamento indifferente alle distinzioni di genere. In tempi in cui si cerca di allontanarsi dalla polarizzazione blu-rosa come riflesso del binarismo uomo-donna, anche il costume da bagno è un mezzo per rappresentare una moda più fluida e a-gender, passando così dall’essere un simbolo prettamente femminista a una più generale retorica di libertà e inclusione assunta prima da diverse componenti della comunità LGBTQIA+ e poi anche dal resto della popolazione, più recentemente. 
L’altro lato della medaglia, l’altrettanto necessario versante critico di questo simbolo, è legato proprio alla questione dell’inclusività. È evidente l’influenza tanto della moda quanto dei social media sulla vita delle persone; dagli acquisti ai pregiudizi, tutto passa per, parte da o arriva alle passerelle e i post online. Per molte donne, infatti, il bikini spesso diventa l’immagine della faticosa e temuta “prova costume”. Dal punto di vista materiale, per via della scarsissima proposta di taglie forti sul mercato; dal punto di vista teorico, a causa della costante comparazione che soprattutto le giovani donne sono portate a compiere rispetto ai milioni di scatti fotografici che si ritrovano a scrollare. La poca educazione a riguardo e la diffusione di standard di bellezza utopici sempre più distanti dalla realtà sana sono la perfetta combinazione per la retorica secondo cui solo i corpi molto magri possono “permettersi” di indossare il bikini. Purtroppo, i movimenti di fat-acceptance non sono ancora riusciti a scardinare dalle menti della maggioranza la convinzione dell’indissolubilità del binomio magrezza-bellezza. 
La speranza è sicuramente che il bikini rimanga solo bandiera della libertà in tutte le sue forme, allontanandosi dalle narrazioni tossiche che lo vincolano a determinati standard di femminilità e di bellezza, simbolo di indipendenza, autodeterminazione e della propria personalità. 
',2,0,'2025-09-16 18:53:17',25,2,1),
 (5,'Nulla da festeggiare',3,'Ogni anno vedo molta confusione e poca consapevolezza in tutto ciò che concerne l’otto marzo; dunque, vorrei approfittare di questo spazio per cercare, nel mio piccolo e con le mie competenze, di fare chiarezza. ','Ogni anno vedo molta confusione e poca consapevolezza in tutto ciò che concerne l’otto marzo; dunque, vorrei approfittare di questo spazio per cercare, nel mio piccolo e con le mie competenze, di fare chiarezza. La giornata internazionale (dei diritti) della donna è tutt’altro rispetto alla descrizione che ci hanno inculcato fin da piccole: innanzitutto, non si vuole commemorare nessun incendio, e soprattutto non abbiamo proprio nulla da festeggiare. Ma andiamo con ordine. 
Quella che ormai è conosciuta dalla generalità della popolazione come la “Festa della donna”, una ricorrenza in cui ci si ricorda solo di comprare il mazzolino di mimose per le nostre care, cela al proprio interno una serie di rivendicazioni tutt’altro che inutili o già guadagnate. 
Ad oggi, l’obiettivo di questa giornata è la commemorazione di tutti i traguardi raggiunti dal genere femminile in campo sociale, politico ed economico, nella speranza di far scaturire in ognuno una riflessione riguardo tutte quelle battaglie che ancora non sono state vinte una volta per tutte. Il divario di genere è tutt’ora un problema sentito, reale e impossibile da ignorare in tutti gli ambiti della nostra vita quotidiana. Pregiudizi, discriminazioni e disparità di trattamento in settori come il lavoro, la salute e la sfera personale sono solo uno del migliaio di motivi per cui è ancora necessario scendere in piazza e manifestare. Da un secolo a questa parte, è proprio la piazza il luogo in cui tutto questo ha preso forma.
Ufficialmente istituita dalle Nazioni Unite solo nel 1975, questa ricorrenza non è sempre stata celebrata l’8 marzo, e non ha sempre assunto il carattere di festività che gli affibbiamo attualmente, anzi. Risale al 28 febbraio 1909 la prima celebrazione della giornata della donna, avvenuta in contesto newyorkese dopo che l’anno precedente le piazze della stessa città si sono riempite di quindicimila donne che pretendevano la garanzia di migliori condizioni lavorative. L’anno seguente, questa occasione fu protagonista di molta attenzione durante l’VIII Congresso Internazionale delle Donne Socialiste di Copenaghen, in cui Clara Zetkin sancì la necessità di istituire una giornata dedicata ai diritti delle donne che fosse la stessa per tutti i Paesi partecipanti al Congresso; l’accordo sulla data, tuttavia, non venne trovato. Negli anni seguenti venne dato adito a tutta una serie di infondate leggende riguardanti l’incendio di una fabbrica statunitense che sarebbe avvenuto l’8 marzo 1908; la realtà dei fatti è ben diversa: questo evento non si è mai verificato, ma potrebbe prendere ispirazione dall’incendio di una fabbrica di tessuti newyorkese “Triangle” avvenuto il 25 marzo 1911 in cui morirono 146 persone, di cui la maggior parte erano donne immigrate italiane ed ebree. Dall’altra parte del mondo, l’8 marzo 1917 le donne di Pietrogrado (attuale San Pietroburgo) marciarono per chiedere la fine della Prima Guerra Mondiale e il diritto al voto, che ottennero poco tempo dopo. Alla fine della rivoluzione bolscevica, l’8 marzo 1922 Vladimir Lenin fondò la Giornata Internazionale dell’operaia. Nello stesso anno, la Giornata Internazionale della Donna arrivò anche in Italia per iniziativa del Partito Comunista Italiano, celebrata realmente solo dal 1946, dopo la fine della Seconda Guerra Mondiale, in quanto ricorrenza considerata prettamente “di sinistra”. Gli eventi appena descritti dimostrano come la prima accezione di questa ricorrenza non fosse concernente le donne come genere oppresso, ma volta a rivendicare i diritti di una parte della popolazione lavoratrice. Come successe anche nella maggior parte dei Paesi occidentali, il femminismo vide i primi spiragli di parità di genere in ambito lavorativo. 
L’usanza quasi esclusivamente italiana della mimosa ha una storia a sé, risalente al secondo dopoguerra. Come da tradizione, il nostro Paese ha sempre sfruttato una simbologia specifica per questo tipo di eventi, e anche all’ora si sentì la necessità di trovare un fiore che rappresentasse la giornata in questione (e che potesse contribuire al “marketing” che la riguardava, indubbiamente). La prima scelta ricadde sulla violetta, prontamente tralasciata in quanto fiore costoso e difficilmente reperibile. A quel punto fu Teresa Mattei a suggerire la mimosa, insieme a Rita Montagnana e Teresa Noce, siccome si trattava di un fiore economico, lo stesso che i partigiani regalavano alle staffette sulle montagne su cui rischiavano di perdere la vita. Con il passare del tempo tante sono state le interpretazioni a riguardo: per alcuni i fiori sui rami rappresentano le donne che combattono unite, per altri la resistenza della mimosa alle basse temperature rappresenta la forza dei movimenti femministi del secolo scorso. Nonostante nessuna di queste sia fondata, mi permetto di dire che a mio parere non c’è da perdere tempo biasimando le persone che si accalcano fuori dai fiorai per comprare i soliti mazzi: regalare fiori è un gesto d’amore che non toglie energie per riflettere sulla situazione contemporanea; gli esseri umani sono perfettamente capaci di fare entrambe le cose, se educati all’importanza della questione. 
Ecco, dunque, che arrivo al punto fondamentale di questo articolo. Fortunatamente negli ultimi anni la Giornata Internazionale (dei diritti) della donna sta riguadagnando la sua connotazione politica, e l’aria di celebrazione che fino a poco fa la caratterizzava si sta di nuovo trasformando in quella sorta di miscuglio tra rabbia, impegno e forza d’animo che spinge il femminismo a urlare a gran voce la necessità di cambiamento da secoli. Non siamo ancora arrivati alla società di pari diritti e pari opportunità che meritiamo, e fino ad allora non accetterò che questa ricorrenza venga chiamata “festa”. C’è ancora bisogno di mettere in discussione il modello patriarcale opprimente che da sempre definisce le nostre vite, c’è ancora bisogno di manifestare per i nostri diritti, di sfruttare la voce di cui siamo dotati tutti e tutte per portare all’attenzione una struttura sociale che incide non sulle sole donne, ma sull’umanità intera. C’è ancora bisogno di ricordare il sangue versato dalle donne che hanno cercato di assumere un ruolo diverso da quello che era stato imposto loro, e farci forza sul loro stesso sdegno per raggiungere la posizione che ci spetta: quella di essere soggetti in mezzo ad altri soggetti, smettendo di essere “l’altro” e di essere definite in relazione a chi ci possiede. Finché anche l’ultima battaglia femminista non sarà vinta, finché la nostra cultura non vedrà la disparità dei generi solo come un ricordo lontano, finché gli “auguri” non saranno accompagnati da una consapevolezza diversa riguardo il privilegio che molti non si rendono conto di avere, non ci sarà nulla da festeggiare. 
',2,0,'2025-09-16 18:54:00',13,'etica',1),
 (6,'Risonanza',3,'Anche questo mese è iniziato in modo turbolento per via della scoperta e diffusione della notizia dell’ennesimo femminicidio, quello di Martina Carbonaro: l’obiettivo di questo articolo è riflettere su questa sensazione e, chissà, trasformare questo senso di impotenza in indignazione attiva. ','Anche questo mese è iniziato in modo turbolento per via della scoperta e diffusione della notizia dell’ennesimo femminicidio, quello di Martina Carbonaro. Noto che spesso, davanti al bombardamento di informazioni a cui veniamo sottoposti tramite i social, la reazione generale è il pensiero che, alla fin fine, il singolo non ha potere nei confronti di un problema sociale e di una responsabilità collettiva. L’obiettivo di questo articolo è riflettere su questa sensazione e, chissà, trasformare questo senso di impotenza in indignazione attiva. Sono molti gli ambiti in cui il contributo del singolo individuo viene percepito come irrilevante al cambiamento generale: la politica (e il risultato dell’ultimo referendum abrogativo ne è la prova), l’etica ambientale, senza dubbio la lotta al patriarcato e tanti altri settori. La mia percezione è che, a questo riguardo, l’attenzione cada sempre sul dato e non sul significato del gesto singolare. Sfrutto una riflessione di Dale Jamieson, attivista ambientale e filosofo analitico, che ritengo abbia saputo esprimere questo concetto meglio di quanto possa fare la sottoscritta: siamo dotati della facoltà di agire nonostante possa sembrare che i nostri singoli gesti non abbiano conseguenze evidenti sul problema in questione; le nostre azioni possono avere un impatto sulle persone che ci circondano, ispirarle e creare di conseguenza una rete di contributi in grado di cambiare il corso della storia. Ciò che facciamo può essere simbolo di sincerità e impegno, e può cambiare il mondo circostante anche se i dati effettivamente non confermano questo cambiamento. È proprio questo il motivo per cui scrivo oggi: nonostante io sia consapevole dell’umile posizione da cui vi parlo, spero che queste parole possano risvegliare la consapevolezza dell’oppressione patriarcale anche in uno solo dei miei lettori o una sola delle mie lettrici, che magari troverà la forza di parlare a un’altra sorella o un altro fratello e dare il via a un’altra catena di riflessione; spero che anche solo un’altra persona trovi tra le mie parole quella risonanza a cui tanto aspirava Carla Lonzi che può, se sfruttata consapevolmente, contribuire alla rivolta. Non è più accettabile rimanere in silenzio davanti alle dinamiche opprimenti che permeano il nostro sistema. In questi giorni ho sentito attribuire la colpa di quei comportamenti violenti a molti fattori: l’incapacità di accettare un rifiuto, di gestire le proprie emozioni negative, di saper reagire in modo razionale e così via; eppure, non mi sembra che gli omicidi di capi uomini che licenziano i propri dipendenti siano un problema sistematico a livello sociale. Il punto è avere il coraggio e la consapevolezza di riconoscere il vero motivo che si nasconde non solo dietro ai femminicidi, ma anche dietro a tutte quelle subdole dinamiche di potere che portano, ogni volta, le donne ad avere paura, a sentirsi sminuite, impotenti, non riconosciute e semplici complementi oggetto di una frase che ha un soggetto maschile e un predicato che le viene imposto: il patriarcato. Viviamo in un orizzonte simbolico fallogocentrico che vuole impedire alle donne di avere l’autocoscienza necessaria per essere più che il mero riflesso della parte maschile dell’umanità. Siamo tutti coinvolti nel portare sulle spalle il peso e le responsabilità di un sistema violento e scrollarsi di dosso le colpe della fine di una vita causata da altre mani non servirà ad altro che a segare le gambe a una rivoluzione necessaria. Dovrebbe essere superfluo ricordarci che al posto di Martina potevo esserci io, o tua sorella, tua madre, tua figlia, la tua fidanzata o la tua coinquilina, perché alla fine dei conti tutte condividiamo la nostra “posizione in tempo reale” quando camminiamo da sole con le chiavi tra le mani e lo spray al peperoncino in borsa, tutte teniamo d’occhio i nostri cocktail senza avere la possibilità di goderci a pieno una serata in discoteca, tutte ci siamo sentite chiamare “signorina”, “tesoro”, o “dolcezza” quando rivestivamo ruoli professionali e tutte ci siamo sentite dire che la nostra rabbia era causata dall’elevata sensibilità femminile o da una condizione biologica che non abbiamo scelto, di cui paghiamo anche un’IVA fin troppo alta. D’altra parte, tutti e tutte abbiamo interiorizzato questa condizione di subordinazione femminile riducendola a normalità, ridendo a battute sessiste o riconducendo un chiaro gioco di potere a una giustificazione tutt’altro che razionale. Prendiamoci la responsabilità delle nostre azioni e sfruttiamola per rendere il mondo un posto più aperto alla possibilità di dare da sé significato alla propria vita. Facciamo leva su quel malessere che troppo spesso viene ricondotto all’incapacità di essere brave madri, brave figlie o brave mogli, perché chi viene riconosciuta come donna eccellente dal patriarcato non può essere tale per il femminismo. Avere gli stessi diritti degli uomini non è abbastanza perché non è la condizione materiale a definirci; al contrario, è la recisione della nostra autodeterminazione, l’impossibilità di vivere i nostri desideri più profondi e di affermarci come esseri umani di pari rispetto e dignità a farlo. Se la logica tutta connotata al maschile è l’unica che abbiamo a disposizione, dobbiamo sovvertirla dall''interno, e fare in modo che “io sono” possa essere affermato da tutta l’umanità, senza che metà di essa sia costretta ad aggiungervi “donna” alla fine. È ora di attivarsi, tutte e tutti, per cambiare il sistema. Sicuramente non lo cambierò io, non lo cambierà nessuno di noi singolarmente, ma forse la nostra unione potrà. E allora alziamo la voce, non solo scendendo in piazza, ma anche parlando con le persone che ci circondano di quello sdegno misto a rabbia che sentiamo dentro davanti all’ennesima notizia di un femminicidio, che non può bruciare il nostro corpo solo per qualche giorno per poi affievolirsi fino alla prossima notizia, perché ha tutte le potenzialità per essere la miccia di qualcosa di più grande.',15,0,'2025-09-16 18:54:47',9,5,1);
INSERT INTO "Authors" ("id","user","age","insta","email","presentation","nickname") VALUES (1,3,23,'https://www.instagram.com/claireeeeeee.__?igsh=MXJjOG9kbHhrcXRi','chiara_dacunto@gmail.com','Ciao! Sono Chiara, ho 23 anni e mi sono laureata in Filosofia alla Sapienza di Roma. Sono la fondatrice di questo magazine, nato dal desiderio di creare uno spazio di confronto libero e critico. Mi occupo principalmente di temi legati all’etica, cercando di esplorare come i principi morali si intrecciano con la vita quotidiana e le sfide del presente.','Claire');
